sourceCode:
/* ==UserStyle==
@name           Sigma-G
@namespace      https://github.com/Catgrills/Sigma-G
@version        1.0.0
@description    An userstyle for Youtube called Sigma-G
@author         Catgrills/Katgrills
@preprocessor uso
@var select theme 'Theme' {
	"Light": `
	:root {
        --body-bg: #FFF !important; 
		--card-bg: #F0F1F5 !important;
        --card-bg-2: #282930 !important;
		--header-bg: #F0F1F5 !important;
		--search-bg: #FFF !important;
        --border-color: #BEBEBE !important;
		--primary-color: #282930 !important;
		--secondary-color: #5E5E5E !important;
 		--fill: #7E7E7E !important;
		--invert: invert(0) !important;
	}
	`,
	"Dark":`
	:root {
	    --body-bg: #272932 !important; 
		--card-bg: #2B3D41 !important;
        --card-bg-2: #4C5F6B !important;
		--header-bg: #2B3D41 !important;
		--search-bg: #2B3D41 !important;
		--border-color: #212121 !important;
		--primary-color: #FFF !important;
		--secondary-color: #CFDBD5 !important;
		--fill: #FFF !important;
		--invert: invert(1) !important;
	}
	`
}
==/UserStyle== */

@-moz-document domain("youtube.com") {
/*[[theme]]*/
/* GENERAL */

    /** DESCRIPTION : set the main font and change others elements **/
    * {
        font-family: "Roboto", "ぼくたちのゴシック２ボールド" !important;
        text-decoration: none;
    }

	*::-moz-focus-inner {
		border: 0;
	}

    a:hover {
     	background: transparent !important;   
    }

    ytd-page-manager > .ytd-page-manager {
     	background: var(--body-bg) !important;   
    }

    #always-shown,
    #autoplay.ytd-compact-autoplay-renderer,
    #button .style-scope.ytd-topbar-menu-button-renderer,
    #collapsible,
    #continuation.ytd-comment-replies-renderer yt-next-continuation.ytd-comment-replies-renderer,
    #country-code.ytd-topbar-logo-renderer,
	.description #more.ytd-expander,
    #footer,
    #guide-links-primary.ytd-guide-renderer,
    #hearted-border.ytd-creator-heart-renderer,
 	#logo.ytd-masthead,
    #masthead-container.ytd-app::after,
    #play-button,
    #primary-items #play-all,
    #sentiment,
    #separator,
    #show-hide-button,
    .style-scope.ytd-button-renderer,
    .super-title.ytd-video-primary-info-renderer,
    #top-level-buttons.ytd-menu-renderer:not(:empty) + #button.ytd-menu-renderer,
    ytd-comment-renderer .more-button.ytd-comment-renderer,
    yt-icon.ytd-badge-supported-renderer,
    yt-icon.ytd-pinned-comment-badge-renderer,
    ytd-thumbnail-overlay-side-panel-renderer {
    	display: none !important;  
    }

/* HEADER */

    /** DESCRIPTION : change the elements of the header **/
    #container.ytd-masthead {
        /*** HEADER CONTAINER ***/
        background: var(--header-bg) !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.15) !important;
    }

    app-drawer[persistent].ytd-app {
        /*** HEADER GUIDE ***/
        box-shadow: 0px 1px 2px rgba(0,0,0,.25) !important;
    }

    #guide-icon.ytd-masthead {
     	fill: var(--fill) !important;   
    }

    #guide-content #guide-button {
     	filter: brightness(0) invert(1) !important;   
    }

    #logo-icon-container.ytd-topbar-logo-renderer {
        background: no-repeat url("https://i.imgur.com/BV5ikoP.png") center !important;
        background-size: cover !important;
        height: 0px !important;
        width: 0px !important;
        padding: 26px 0px 0px 116px !important;
    }

    #guide-content.ytd-app,
    #guide-content #sections {
        background: #282930 !important;
    }

    app-drawer.ytd-app:not([persistent]) #header.ytd-app,
    #sections.ytd-guide-renderer > .ytd-guide-renderer:not(:last-child) {
        border-bottom: 1px dotted #60646D !important;
    }

    #sections.ytd-guide-renderer > .ytd-guide-renderer:not(:last-child) {
        padding-bottom: 15px !important;
    }

    #sections.ytd-guide-renderer > .ytd-guide-renderer:nth-last-child(2),
    #sections.ytd-guide-renderer > .ytd-guide-renderer:last-child {
    	display: none !important;
	}

    #guide-content .yt-simple-endpoint.style-scope.yt-formatted-string {
        color: #FFF !important;
        font-family: "Roboto Condensed" !important;
        font-size: 17pt !important;
    }

    .title.ytd-guide-entry-renderer,
    .guide-entry-count.ytd-guide-entry-renderer {
        color: #FFF !important;
        font-family: "Roboto Condensed" !important;
        font-size: 15pt !important;
        height: 22px !important;
    }

    .guide-entry-badge.ytd-guide-entry-renderer {
        color: #FFF !important;
        height: 26px;
        width: 26px;
        margin-right: -7px !important;
    }

    ytd-searchbox[mode="legacy"] #container.ytd-searchbox {
        /*** HEADER SEARCH BOX ***/
    	background: transparent !important;
        border: none !important;
        box-shadow: none !important;
    }

    #container.ytd-masthead #search {
        color: var(--secondary-color) !important;
        font-family: "Roboto Condensed" !important;
        font-size: 15pt !important;
	}

    #container.ytd-masthead #search-icon-legacy.ytd-searchbox {
    	background: transparent !important;
        border: none !important;
    }

    #search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
        color: var(--secondary-color) !important;
        height: 25px !important;
        width: 25px !important;
    }

    #container.ytd-masthead ytd-searchbox[mode="legacy"] #search-icon-legacy.ytd-searchbox:hover {
    	box-shadow: none;    
    }

	.gstl_0.sbdd_a,
	.sbdd_b {
        border: none !important;
        border-radius: 5px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
	}

	.sbdd_b .sbsb_a {
        background: var(--search-bg) !important;
        border: none !important;
        border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		padding: 15px 10px !important;
		margin-top: -2px !important;
	}

	.sbdd_b .sbsb_a * {
        color: var(--primary-color) !important;
        font-family: "Roboto Condensed" !important;
		font-size: 15pt !important;
		font-weight: normal !important;
		line-height: 24pt !important;
	}

    #container.ytd-masthead button.yt-icon-button > yt-icon {
        /*** HEADER NOTIFICATION BUTTON ***/
        background: no-repeat url("https://i.imgur.com/Qo3NJzX.png") center !important;
        background-size: cover !important;
        border-radius: 50% !important;
        height: 0px !important;
        width: 0px !important;
        padding: 42px 0px 0px 42px !important;
        margin-top: -9px !important;
        margin-left: -10px !important;
        opacity: .5 !important;
        filter: var(--invert) !important;
    }

/* HOME */

	/** DESCRIPTION : change the elements of the home page **/
    ytd-two-column-browse-results-renderer.ytd-browse {
        /*** HOME CONTAINER ***/
        width: 80% !important;
        margin-top: 35px !important;
    }

    ytd-two-column-browse-results-renderer #avatar {
        /*** HOME SECTION AVATAR ***/
        box-shadow: 0px 1px 2px rgba(0,0,0,.3) !important;
    	margin-right: 15px !important;   
    }

    ytd-two-column-browse-results-renderer #avatar img.yt-img-shadow {
    	box-shadow: 0px 1px 2px rgba(0,0,0,1) !important;
        height: 40px !important;
        width: 40px !important;
	}

    ytd-two-column-browse-results-renderer #title.ytd-shelf-renderer {
        /*** HOME SECTION TITLE ***/
        color: #3D8EB9 !important;
        font-family: "Roboto Condensed", "ぼくたちのゴシック２ボールド" !important;
        font-size: 18pt !important;
        max-height: 30px !important;
        height: 25px !important;
        max-width: 100% !important;
        overflow: hidden !important;
        text-overflow: ellipsis !important;
        white-space: nowrap !important;
        margin-top: -3px !important;
    }

    ytd-two-column-browse-results-renderer #title-annotation.ytd-shelf-renderer {
        font-family: "Roboto Condensed", "ぼくPたちのゴシック２ボールド" !important;
        font-size: 17pt !important;  
        margin-top: -6px !important;
        margin-left: 15px !important;
        overflow: visible !important;
    }

    ytd-two-column-browse-results-renderer a.yt-simple-endpoint.yt-formatted-string {
        color: var(--secondary-color) !important;
        font-family: "Roboto Condensed", "ぼくたちのゴシック２ボールド" !important;
        font-size: 17pt !important;   
        overflow: visible !important;
    }

    #top-level-buttons.ytd-menu-renderer #button yt-icon {
        fill: var(--fill) !important;    
    }

    ytd-two-column-browse-results-renderer #items.yt-horizontal-list-renderer > .yt-horizontal-list-renderer,
    ytd-two-column-browse-results-renderer #items.ytd-grid-renderer > ytd-grid-video-renderer.ytd-grid-renderer {
        /*** HOME SECTION CONTENT ***/
        padding-right: 10px !important;
    }

    #scroll-container.yt-horizontal-list-renderer {
        margin-top: 35px !important;
    }

    #contents.ytd-section-list-renderer > .ytd-section-list-renderer:not(:last-child):not(ytd-page-introduction-renderer) {
        border-bottom: 1px dotted #BEBEBE !important;
    }

    #thumbnail.ytd-thumbnail {
        /*** HOME SECTION CONTENT THUMBNAILS ***/
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.1) !important;
    }

    ytd-two-column-browse-results-renderer #byline-container {
        /*** HOME SECTION CONTENT INFORMATIONS ***/
        line-height: 22px !important;
        max-height: 30px !important;
        margin-top: -5px !important;
        margin-bottom: 5px !important;
    }

    ytd-two-column-browse-results-renderer #metadata-line {
        display: none !important;
    }

    ytd-two-column-browse-results-renderer #details.ytd-grid-video-renderer a.ytd-grid-video-renderer {
        color: var(--primary-color) !important;
        font-family: "Roboto Condensed", "ぼくたちのゴシック２ボールド" !important;
        font-size: 14pt !important;
        line-height: 24px !important;
        overflow: hidden !important;
        text-overflow: ellipsis !important;
        white-space: nowrap !important;
    }

    yt-formatted-string[ellipsis-truncate] a.yt-formatted-string:last-child {
        color: var(--primary-color) !important;
        font-family: "Roboto Condensed", "ぼくたちのゴシック２ボールド" !important;
        font-size: 14pt !important;
    }

    ytd-two-column-browse-results-renderer .badge-style-type-live-now.ytd-badge-supported-renderer,
    ytd-two-column-search-results-renderer .badge-style-type-live-now.ytd-badge-supported-renderer {
        /*** HOME CONTENT BADGE ***/
        background: #F29B34 !important;
        border: none !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.1) !important;
        color: #FFF !important;
        font-family: "Roboto Condensed" !important;
        font-size: 14pt !important;
        padding: 14px 16px !important;
        margin-top: 10px !important;
        white-space: nowrap !important;
    }

    ytd-two-column-search-results-renderer .badge-style-type-live-now.ytd-badge-supported-renderer * {
        font-family: "Roboto Condensed" !important;   
        font-weight: bold !important;
    }

    ytd-toggle-button-renderer.style-compact-gray[is-paper-button] {
      	background: #60646D !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.1) !important;
        color: #FFF !important;
        font-family: "Roboto Condensed" !important;
        font-size: 14pt !important;
        padding: 5px 10px !important;
        margin-top: 10px !important;        
    }

    ytd-two-column-browse-results-renderer .badge-style-type-live-now.ytd-badge-supported-renderer *,
    ytd-toggle-button-renderer.style-compact-gray[is-paper-button] * {
        font-family: "Roboto Condensed" !important;
        font-weight: bold;
    }

    ytd-two-column-browse-results-renderer .badge-style-type-collection.ytd-badge-supported-renderer {
     	display: none !important;   
    }


    #toggle.ytd-grid-renderer {
        /*** HOME MORE BUTTON ***/
        background: #2C82C9 !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.2) !important;
        height: 50px !important;
        line-height: 48px !important;
        width: 200px !important;
    }

    paper-button.yt-next-continuation *,
    #toggle.ytd-grid-renderer * {
        color: #FFF !important;
        font-family: "Roboto Condensed" !important;
        font-size: 14pt !important;
        font-weight: bold;
    }

    .arrow.yt-horizontal-list-renderer {
        /*** HOME ARROW BUTTON ***/
        background: var(--card-bg) !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.3) !important;
    }

    #right-arrow * {
        display: block !important;
    }

    #right-arrow yt-icon {
         fill: var(--fill) !important;
    }

/* TRENDING/SUBSCRIPTIONS */

    /** DESCRIPTION : change the elements of the trending/subscriptions page **/
	ytd-two-column-browse-results-renderer.ytd-browse #dismissable.ytd-video-renderer {
        background: var(--card-bg) !important;
        border: 3px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.15) !important;
        padding: 15px !important;
    }

    ytd-two-column-browse-results-renderer.ytd-browse #video-title.ytd-video-renderer {
        color: var(--primary-color) !important;
        font-family: "Roboto Condensed", "ぼくたちのゴシック２ボールド" !important;
        font-size: 16pt !important;
        height: 26px !important;
	}

    #description-text.ytd-video-renderer {
        font-family: "Roboto Condensed", "ぼくたちのゴシック２ボールド" !important;
     	font-size: 14pt !important;   
        height: 20px !important;
        overflow: hidden !important;
        white-space: nowrap !important;
        text-overflow: ellipsis !important;
    }

    #description-text.ytd-video-renderer * {
        font-family: "Roboto Condensed", "ぼくたちのゴシック２ボールド" !important;
    }

    ytd-two-column-browse-results-renderer.ytd-browse .text-wrapper.ytd-video-renderer .ytd-badge-supported-renderer {
     	display: none !important;   
    }

    ytd-two-column-browse-results-renderer.ytd-browse a[is="yt-endpoint"].ytd-button-renderer, 
    ytd-two-column-browse-results-renderer.ytd-browse a[is="yt-endpoint"].ytd-button-renderer * {
        display: block !important;
    }

    ytd-two-column-browse-results-renderer.ytd-browse a[is="yt-endpoint"].ytd-button-renderer #tooltip.paper-tooltip {
     	display: none !important;   
    }

/* SEARCH */

    /** DESCRIPTION : change the elements of the search page **/
    ytd-two-column-search-results-renderer[center-results] #primary.ytd-two-column-search-results-renderer {
        margin-left: 10px !important;
        margin-right: 10px !important;
    }

    ytd-two-column-search-results-renderer ytd-search-sub-menu-renderer {
        border-bottom: 1px dotted #BEBEBE !important;
    	padding-bottom: 8px !important;
    }

    ytd-two-column-search-results-renderer ytd-shelf-renderer.ytd-item-section-renderer {
        border-top: 1px dotted #BEBEBE !important;
        border-bottom: 1px dotted #BEBEBE !important;         
    }

    ytd-search-sub-menu-renderer #result-count.ytd-search-sub-menu-renderer,
    ytd-search-sub-menu-renderer #title-container.ytd-shelf-renderer *,
    ytd-two-column-search-results-renderer ytd-shelf-renderer.ytd-item-section-renderer #title-container.ytd-shelf-renderer * {
        color: #3D8EB9 !important;
        font-family: "Roboto Condensed", "ぼくたちのゴシック２ボールド" !important;
    	font-size: 19pt !important;
        overflow: visible !important;
	}

    ytd-search-sub-menu-renderer ytd-toggle-button-renderer[is-paper-button] {
        color: var(--secondary-color) !important;
        font-size: 15pt !important;
		margin-top: 5px !important;
    }

    ytd-search-sub-menu-renderer ytd-toggle-button-renderer[is-paper-button] * {
        font-family: "Roboto Condensed" !important;
        font-weight: normal !important;
        text-transform: Capitalize !important;
    }

    #collapse * {
        font-family: "Roboto Condensed" !important;
        font-size: 15pt !important;
    }

    yt-did-you-mean-renderer * {
        color: var(--secondary-color);
        font-family: "Roboto Condensed" !important;
        font-size: 15pt !important;
    }

    ytd-section-list-renderer.ytd-two-column-search-results-renderer #dismissable.ytd-video-renderer {
        background: var(--card-bg) !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.15) !important;
        padding: 15px !important;
    }

    ytd-channel-renderer {
        background: var(--card-bg) !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.15) !important;
        padding-right: 15px !important;
        padding-bottom: 15px !important;
        margin-top: 20px !important;
    }

    ytd-section-list-renderer.ytd-two-column-search-results-renderer ytd-radio-renderer.ytd-item-section-renderer,
    ytd-section-list-renderer.ytd-two-column-search-results-renderer ytd-playlist-renderer.ytd-item-section-renderer {
        background: var(--card-bg-2) !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.15) !important;
        padding: 15px !important;        
    }

    ytd-section-list-renderer.ytd-two-column-search-results-renderer ytd-radio-renderer.ytd-item-section-renderer *,
    ytd-section-list-renderer.ytd-two-column-search-results-renderer ytd-playlist-renderer.ytd-item-section-renderer * {
        color: #FFF !important;
    }

    ytd-section-list-renderer.ytd-two-column-search-results-renderer #dismissable.ytd-video-renderer #video-title.ytd-video-renderer,
    ytd-section-list-renderer.ytd-two-column-search-results-renderer ytd-radio-renderer.ytd-item-section-renderer #video-title.ytd-radio-renderer,
    ytd-section-list-renderer.ytd-two-column-search-results-renderer ytd-playlist-renderer.ytd-item-section-renderer #video-title.ytd-playlist-renderer,
    ytd-section-list-renderer.ytd-two-column-search-results-renderer #channel-title.ytd-channel-renderer span.ytd-channel-renderer {
        color: var(--primary-color) !important;
        font-family: "Roboto Condensed", "ぼくたちのゴシック２ボールド"  !important;
        font-size: 17pt !important;
        height: 26px !important;
        margin-bottom: 10px !important;
    }

    ytd-section-list-renderer.ytd-two-column-search-results-renderer ytd-radio-renderer.ytd-item-section-renderer #video-title.ytd-radio-renderer,
    ytd-section-list-renderer.ytd-two-column-search-results-renderer ytd-playlist-renderer.ytd-item-section-renderer #video-title.ytd-playlist-renderer {
        color: #FFF !important;
    }


    ytd-section-list-renderer.ytd-two-column-search-results-renderer #dismissable.ytd-video-renderer yt-formatted-string[ellipsis-truncate] a.yt-formatted-string:last-child {
        font-size: 15pt !important;
    }

    ytd-section-list-renderer.ytd-two-column-search-results-renderer #dismissable.ytd-video-renderer #metadata-line.ytd-video-meta-block {
        color: var(--secondary-color) !important;
        margin-top: 7px !important;
        margin-left: 10px !important;
    }

    ytd-section-list-renderer.ytd-two-column-search-results-renderer #dismissable.ytd-video-renderer #metadata-line.ytd-video-meta-block *,
    ytd-section-list-renderer.ytd-two-column-search-results-renderer #metadata.ytd-channel-renderer * {
        color: var(--secondary-color) !important;
        font-family: "Roboto Condensed", "ぼくたちのゴシック２ボールド"  !important;
        font-size: 15pt !important;
    }

    #metadata-line.ytd-video-meta-block > .ytd-video-meta-block:not(:last-of-type)::after,
    #metadata.ytd-channel-renderer > .ytd-channel-renderer:not(:last-of-type):not(:empty)::after {
        visibility: hidden;
    }

    ytd-section-list-renderer.ytd-two-column-search-results-renderer #list.ytd-radio-renderer *,
    ytd-section-list-renderer.ytd-two-column-search-results-renderer #list.ytd-playlist-renderer *,
    ytd-section-list-renderer.ytd-two-column-search-results-renderer #view-more.ytd-playlist-renderer *,
    #description.ytd-channel-renderer {
        font-family: "Roboto Condensed", "ぼくたちのゴシック２ボールド"  !important;  
        font-size: 14pt !important;
        line-height: 25px !important;
    }

    #description.ytd-channel-renderer {
        overflow: hidden !important;
        text-overflow: ellipsis !important;
        white-space: nowrap !important;
    }

    ytd-section-list-renderer.ytd-two-column-search-results-renderer #dismissable.ytd-video-renderer .badge-style-type-simple.ytd-badge-supported-renderer,
    #details.ytd-grid-video-renderer .badge-style-type-simple.ytd-badge-supported-renderer {
        background: #2CC990 !important;
        border: none !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.1) !important;
        color: #FFF !important;
        font-family: "Roboto Condensed" !important;
        font-size: 14pt !important;
        font-weight: bold;
        padding: 14px 16px !important;
        margin-top: 10px !important;
        text-transform: uppercase !important;
    }

    ytd-section-list-renderer.ytd-two-column-search-results-renderer #dismissable.ytd-video-renderer .badge-style-type-simple.ytd-badge-supported-renderer *,
    #details.ytd-grid-video-renderer .badge-style-type-simple.ytd-badge-supported-renderer * {
        font-family: "Roboto Condensed" !important;
    }

    ytd-section-list-renderer.ytd-two-column-search-results-renderer  #more.ytd-vertical-list-renderer yt-formatted-string.ytd-vertical-list-renderer {
        background: #2C82C9 !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.2) !important;
        height: 40px !important;
        line-height: 40px !important;
        width: 130px !important;
        text-align: center !important;
    }

